sqls.sql:
insert into "STY_日次情報" values ( :today
, (select count(*) from "STY_単語情報")
, ( select count(*) from ( select book,classification,wordseq from "V_単語情報リスト" where kind in ('A.勉強','B.中日訳英','C.音訳英','D.英訳中') and level = '1.簡単' AND wrong_flg = '-' group by book,classification,wordseq having count(distinct kind) = 4 ) as t1 ) , ( select count(*) from ( select book,classification,wordseq from "V_単語情報リスト" where kind in ('A.勉強','B.中日訳英','C.音訳英','D.英訳中') and level = '1.簡単' group by book,classification,wordseq having count(distinct kind) = 4 and sum(case when wrong_flg = '-' then 1 else 0 end) < 4 and sum(case when wrong_flg = '○' then 1 else 0 end) < 4
) as t2 ) , ( select count(*) from ( select book,classification,wordseq from "V_単語情報リスト" where kind in ('A.勉強','B.中日訳英','C.音訳英','D.英訳中') and level = '1.簡単' AND wrong_flg = '○' group by book,classification,wordseq having count(distinct kind) = 4 ) as t3 ), null, null, null, null, null, null, null, null, null, null, null, null, :userid, :userid, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP
)
